    // Time tracker
    var timeTracker = {

        // Default configurations
        config: {
            scope:                                  ""
        },

        // Initialize
        init: function() {

            // Event listener
            timeTracker.eventListener();

        },

        // Event listener
        eventListener: function() {

            // Click on delete button

            jQuery( "table [data-timetrack]" ).on( "click", function( event ) {

                // Set scope
                timeTracker.config.scope        =   jQuery( this ),
                modal_type                      =   jQuery( this ).attr( "data-timetrack" );

                // Start
                if( modal_type == "start" ) timeTracker.showStartModal();

                // Stop
                if( modal_type == "stop" )  timeTracker.showStopModal();

                // Prevent hash in URI
                event.preventDefault();

            });
        },

        // Show start modal
        showStartModal: function() {

            // Get configurations
            var config                          =   timeTracker.config,
                scope                           =   config.scope,
                locale                          =   {
                    title:                          "Stempl ind?",
                    body:                           "Vil du stemple ind p√• denne opgave?",
                    buttons: {
                        accept:                     "Ja",
                        cancel:                     "Annuller"
                    }
                };

            // Confirm modal
            modalizr.confirm({
                title:                              locale.title,
                body:                               locale.body,
                buttons: {

                    // Accept button
                    accept: {
                        value:                      locale.buttons.accept,
                        callback:                   function() { timeTracker.startTimer(); }
                    },

                    // Cancel button
                    cancel: {
                        value:                      locale.buttons.cancel
                    }
                }
            });

        },

        // Start time
        startTimer: function() {

            // Get configurations
            var config                          =   timeTracker.config,
                scope                           =   config.scope,
                node_id                         =   scope.attr( "data-nid"),
                redirect_url                    =   "/time_tracker/start/" + node_id;

            // Create ajax instance
            jQuery.ajax({
                url:                                redirect_url,
                type:                               "get",
                datatype:                           "json"
            })

            // Finished ajax
            .done( function( data ) {

                // Errors occured
                if( data.error === true ) {

                    // Return false
                    return false;

                }

                // Hide modal
                jQuery( "#modalizr").modal( "hide" );

            });

        },





        // Show stop modal
        showStopModal: function() {

            // Get configurations
            var config                          =   timeTracker.config,
                scope                           =   config.scope,
                locale                          =   {
                    title:                          "Stempl ud?",
                    body:                           "Vil du stemple ud fra denne opgave?",
                    buttons: {
                        accept:                     "Ja",
                        cancel:                     "Annuller"
                    }
                };

            // Confirm modal
            modalizr.confirm({
                title:                              locale.title,
                body:                               locale.body,
                buttons: {

                    // Accept button
                    accept: {
                        value:                      locale.buttons.accept,
                        callback:                   function() { timeTracker.stopTimer(); }
                    },

                    // Cancel button
                    cancel: {
                        value:                      locale.buttons.cancel
                    }
                }
            });

        }

    }